
public class Snumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Declare the variable for a and b
		int a=20,b=10;
		//Adding a and b assign to value to a
		a = a + b;
		//subtract a and b assign value to b;
        b = a - b;
      //subtract a and b assign value to a;
        a = a - b;
        System.out.println("After swapping:"
                           + " a = " + a + ", b = " + b);


	}

}
