
public class swapnumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10, b=20,c = 0;
		//Assign value of a to c;
		c=a;
		//Assign value of a to b;
		a=b;
		//Assign value of b to c;
		b=c;	
		 System.out.println("After swapping:"
                 + " a = " + a + ", b = " + b);
	}

}
